mapper.select:
select count(*)
from ljjcjava2.issue
where state = #{state};

select avg(DATEDIFF(close_date, create_date)) as avgTime
from ljjcjava2.issue
where state = 'closed';

select max(DATEDIFF(close_date, create_date)) as maxTime
from ljjcjava2.issue
where state = 'closed';

select stddev(DATEDIFF(close_date, create_date)) as stddevTime
from ljjcjava2.issue
where state = 'closed';
